[ORG 0x7C00]

xor ax, ax
mov ds, ax
mov es, ax
mov ss, ax
mov sp, 0x7c00

mov ah, 2		;read disk
mov al, 24		;number of sectors to read
mov ch, 0		;C cylinder
mov cl, 2		;S sector
mov dh, 0		;H head
;mov dl, 0x80		;drive, already set on boot drive by bios
mov bx, 0x7e00		;[es:bx] adress to load
int 0x13

jmp 0x7e00

times 510-($-$$) db 0
db 0x55, 0xAA


	mov ah, 0x00
	mov al, 0x03
	int 0x10

	mov di, 0
	mov si, ok_message
	call print_str
	mov di, 320
	mov si, pendu_7
	call print_str

	call rng
	mov ax, di
	mov di, 160
	mov cx, 835
	div cx
	mov ax, dx
	mov cx, 2
	mul cx
	mov bx, words_offsets
	add bx, ax
	mov word si, [es:bx]
	add si, words_offsets
	mov [word_to_find], si
	call print_str

hang:
	jmp hang

ok_message: db "Bienvenue", 0


print_hex:		;print the content of si, not a pointed value
	push ax
	push cx
	xor cx, cx
	mov ax, 0xb800
	mov ds, ax
print_hex_loop:
	cmp cx, 4
	je print_hex_end
	rol si, 4
	mov ax, si
	and ax, 0xf
	cmp al, 10
	jl print_hex_number
	add al, 7
print_hex_number:
	add al, 48
	mov byte [ds:di], al
	inc di
	mov byte [ds:di], 0x20
	inc di
	inc cx
	jmp print_hex_loop
print_hex_end:
	pop cx
	pop ax
	ret

print_str:		;pointed by es:si
	push ax
	mov ax, 0xb800
	mov ds, ax
print_str_char:
	mov byte al, [es:si]
	or al, al
	jz print_str_end
	mov byte [ds:di], al
	inc di
	mov byte [ds:di], 0x0f
	inc di
	inc si
	jmp print_str_char
print_str_end:
	pop ax
	ret


rng:
	push ax
	push cx
	push dx
	xor ax, ax
	int 0x1A
	mov ax, dx
	mov dx, 0
	mov cx, 75
	mul cx
	shl edx, 16
	mov dx, ax
	mov eax, edx
	xor edx, edx
	mov ecx, 65537
	div ecx
	xor ecx, ecx
	mov edi, edx
	and edi, 0x0000ffff
	xor edx, edx
	pop dx
	pop cx
	pop ax
	ret


